choose.when:
SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL

SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL
    AND ROWNUM <= 500

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL
LIMIT 500

UPDATE ${msg_table}
SET STATUS = '2',
    RESPONSE_DT = SYSDATE
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '2',
    RESPONSE_DT = NOW()
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE MSG_ID = #{msgid}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE MSG_ID = #{msgid}

SELECT COUNT(1) as cnt
FROM ${msg_table}
WHERE STATUS = '9'

SELECT COUNT(1) as cnt
FROM ${msg_table}
WHERE STATUS = '9'

UPDATE ${msg_table}
SET SEND_GROUP = #{group_no}
WHERE STATUS = '9'
    AND ROWNUM < 500

UPDATE ${msg_table}
SET SEND_GROUP = #{group_no}
WHERE STATUS = '9'
LIMIT 500

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE SEND_GROUP = #{group_no}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '1',
SEND_GROUP = #{group_no}
WHERE STATUS = '0'
AND RESERVE_DT <= SYSDATE
AND MESSAGE_TYPE = #{msg_type}
AND SEND_GROUP IS NULL
AND (FI_IMAGE_PATH IS NULL OR (IMAGE_URL IS NOT NULL AND FI_IMAGE_PATH IS NOT NULL))
AND ROWNUM <= 500

UPDATE ${msg_table}
    SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SEND_GROUP IS NULL
    AND (FI_IMAGE_PATH IS NULL OR (IMAGE_URL IS NOT NULL AND FI_IMAGE_PATH IS NOT NULL))
LIMIT 500

UPDATE ${msg_table}
    SET IMAGE_URL = #{ft_image_url}
WHERE MSG_ID = #{msgid}

UPDATE ${msg_table}
    SET IMAGE_URL = #{ft_image_url}
WHERE MSG_ID = #{msgid}

UPDATE ${msg_table}
SET RESULT = '${ft_image_code}',
    RESULT_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS'),
    STATUS = 'F',
    RESULT_MESSAGE = '${img_err_msg}'
WHERE MSG_ID = #{msgid}

UPDATE ${msg_table}
SET RESULT = '${ft_image_code}',
    RESULT_DT = NOW(),
    STATUS = 'F',
    RESULT_MESSAGE = '${img_err_msg}'
WHERE MSG_ID = #{msgid}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE MSG_ID = #{msgid}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE MSG_ID = #{msgid}
