choose.when:
SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE = #{msg_type}
    AND SMS_KIND = #{sms_kind}
    AND SEND_GROUP IS NULL

SELECT count(1) AS cnt
FROM ${msg_table}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SMS_KIND = #{sms_kind}
    AND SEND_GROUP IS NULL

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= SYSDATE
    AND MESSAGE_TYPE = #{msg_type}
    AND SMS_KIND = #{sms_kind}
    AND SEND_GROUP IS NULL
    AND ROWNUM <= 500

UPDATE ${msg_table}
SET STATUS = '1',
    SEND_GROUP = #{group_no}
WHERE STATUS = '0'
    AND RESERVE_DT <= NOW()
    AND MESSAGE_TYPE = #{msg_type}
    AND SMS_KIND = #{sms_kind}
    AND SEND_GROUP IS NULL
LIMIT 500

UPDATE ${msg_table}
    SET STATUS = '2',
    RESPONSE_DT = CURRENT_TIMESTAMP
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
    SET STATUS = '2',
    RESPONSE_DT = CURRENT_TIMESTAMP
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

UPDATE ${msg_table}
SET STATUS = '0',
    SEND_GROUP = NULL,
    RESPONSE_DT = NULL
WHERE SEND_GROUP = #{group_no}

INSERT INTO ${log_table}
SELECT * FROM ${msg_table}
WHERE MSG_ID = #{msgid}

